mapper.select:
SELECT d.record_seq,  d.user_seq, d.ex_no, m.user_id, e.ex_name, d.date, d.weight, d.reps, d.sets, d.volume
  FROM ex_daily as d
  LEFT JOIN sf_member as m
    ON m.member_seq = d.user_seq
  LEFT JOIN sf_ex as e
    ON e.ex_no = d.ex_no
 WHERE user_id = #{userId}
   AND e.ex_no = #{exNo}
 ORDER BY d.date;
